sys_script_fix.script:
gs.invalidateCache();
var loanerRecs = new GlideRecord('x_cdltd_loaner_req_loaner_request'); //new Glide record for table
loanerRecs.addQuery('state', '!=', 3); //Where state is not Closed Complete
loanerRecs.setLimit(3); //limit returned records to 1 record
loanerRecs.query();

while (loanerRecs.next()) {
    loanerRecs.setValue('state', 3); //set the state to Closed Complete
    loanerRecs.update(); //update the database
}
